import React, { Component } from 'react';


class LogIn extends Component {

  constructor(props) {
    super(props);
    this.state = {
      try_email: '',
      try_password: ''
    }

  }

  // Keep the sign in registration
  componentDidMount() {
  const try_email = localStorage.getItem('email');
  const try_password = localStorage.getItem('password');
  this.setState({ try_email, try_password });
  }


  handleChange = (event) => {
     const input = event.target;
     const value = input.value;

     this.setState({ [input.id]: value })
  }

  submit = () => {
    const real_email = localStorage.getItem('email');
    const real_password = localStorage.getItem('password');
    const { try_email, try_password } = this.state;
    if (try_email === real_email &&  try_password === real_password)
    {
      this.props.history.push("/");

    }
    else{
      console.log("error");
    }
  }

  render() {
    return (
      <div >
        <input id="email" type="text" onChange={this.handleChange}/>
        <br/>
        <input id="password" type="password" onChange={this.handleChange}/>
        <br/>
        <button onClick={this.login}>Login</button>
      </div>
    );
  }

}
export default LogIn;
