import React, { Component } from 'react';


class SignIn extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: ''
    }

  }

  handleChange = (e) => {
     const input = e.target;
     const value = input.value;

     this.setState({ [input.id]: value })

     console.log(this.state)

  }

  submit = (e) => {
    e.preventDefault();
    this.state.verifyUser(this.state);
  }

  render() {
    return (
      <div >
        <form onSubmit={this.submit}>

          <label>email:</label>
          <input type="text" id="email" value={this.state.email} onChange={this.handleChange} />

          <label>password:</label>
          <input type="text" id="password" value={this.state.password} onChange={this.handleChange} />

          <button> Login </button>
        </form>
      </div>
    );
  }

}
export default SignIn;
