import React, { Component } from 'react';


class LogIn extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: ''
    }

  }

  componentDidMount() {
  const email = localStorage.getItem('email');
  const password = localStorage.getItem('password');
  this.setState({ email, password });
  }

  handleClick = () => {
  this.props.history.push("/");
  }

  handleChange = (e) => {
     const input = e.target;
     const value = input.value;

     this.setState({ [input.id]: value })
  }

  submit = (e) => {
    e.preventDefault();
    const { email, password } = this.state;
    localStorage.setItem('try_email', email);
    localStorage.setItem('try_password', password);
    //this.state.verifyUser(this.state);
  }

  render() {
    return (
      <div >
        <form onSubmit={this.submit}>

          <label>email:</label>
          <input type="text" id="email" value={this.state.email} onChange={this.handleChange} />

          <label>password:</label>
          <input type="text" id="password" value={this.state.password} onChange={this.handleChange} />

          <button type= "submit" onClick={this.handleClick} > Sign In </button>
        </form>
      </div>
    );
  }

}
export default LogIn;
