import React, { Component } from 'react';


class SignIn extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: ''
    }

  }

  handleClick = () => {
    this.props.history.push("/LogIn");
  }

  handleChange = (e) => {
     const input = e.target;
     const value = input.value;

     this.setState({ [input.id]: value })

     console.log(this.state)

  }

  submit = () => {
    const { email, password } = this.state;
    localStorage.setItem('email', email);
    localStorage.setItem('password', password);
    //this.state.verifyUser(this.state);
  }

  render() {
    return (
      <div >
        <form onSubmit={this.submit}>

          <label>email:</label>
          <input type="text" id="email" value={this.state.email} onChange={this.handleChange} />

          <label>password:</label>
          <input type="text" id="password" value={this.state.password} onChange={this.handleChange} />

          <button type= "submit" onClick={this.handleClick} > Sign In </button>
        </form>
      </div>
    );
  }

}
export default SignIn;
