const initialState = {
  user : {
    mail : null,
    password : null
  }
}
function toggleUser (state = initialState, action){
  let nextState
  switch (action.type){
    case 'ADD_USER':
      nextState = {user : action.value}
      return nextState || nextState
    default:
      return state
  }
}
export default toggleUser
